mapper.select:
select
g.game_id, game_title, game_dev, game_price, game_discount_rate, game_sub_text, game_main_text, game_rate, game_thumb_image, game_category_id1, game_category_id2, game_category_id3, game_category_id4, game_intro_image1, game_intro_image2, game_intro_image3, game_intro_image4, game_intro_image5, game_release_date, game_update_date, game_sale_count, game_total_earnings
from game_info g
left join review_list r on g.game_id = r.game_id
AND game_title like CONCAT('%', #{keyword}, '%')
AND game_discount_rate > 0
AND ( game_category_id1 in
#{category}
OR game_category_id2 in
#{category}
OR game_category_id3 in
#{category}
OR game_category_id4 in
#{category}
)
AND (game_price * (100 - game_discount_rate) / 100) >= #{lowPriceFilter}
AND (game_price * (100 - game_discount_rate) / 100) <= #{highPriceFilter}
group by g.game_id
having round(avg(review_star_rating)) = #{starFilter}
order by g.game_id desc
order by game_sale_count desc
order by AVG(review_star_rating) desc
order by game_title
order by (game_price * (100 - game_discount_rate) / 100)
order by (game_price * (100 - game_discount_rate) / 100) desc
order by game_discount_rate desc
limit #{start}, #{cntPerPage}

 		select count(*)
 		from(
 		select count(*)
 		from game_info g

 		left join review_list r
 		on g.game_id = r.game_id





				AND game_title like CONCAT('%', #{keyword}, '%')



				AND game_discount_rate > 0



				AND( 
				game_category_id1 in

			    	 #{category}

				OR game_category_id2 in

			    	 #{category}

				OR game_category_id3 in

			    	 #{category}

				OR game_category_id4 in

			    	 #{category}

				)



				AND (game_price * (100 - game_discount_rate) / 100) >= #{lowPriceFilter}


				AND (game_price * (100 - game_discount_rate) / 100) <= #{highPriceFilter}




		group by g.game_id


			having round(avg(review_star_rating)) = #{starFilter}

		) as tb
